get_unique_article_tag_count.map:
function (doc, meta) {
  if(doc._type == 'a') {
    for(i=0; i<doc.tags.length; i++) {
      emit(doc.tags[i], null);
    }
  }
}

get_unique_article_tag_count.reduce:
function(key, values, rereduce) {
  var sum = 0;
  var dict = {};
  for(k in key) {
    if(dict[key[k]] != 1) {
	sum += 1;
      dict[key[k]] = 1;
    }
  }
  return sum;
}

get_aid_from_tag.map:
function (doc, meta) {
  if(doc._type=='a') {
    for(tag in doc.tags) {
      emit(doc.tags[tag], null);
    }
  }
}

get_end_by_uid.map:
function (doc, meta) {
  if(doc._type == "e") {
    emit([doc.tuid, doc.skill], null);
  }
}

